import React from 'react'
import styles from './Cards.module.css'

function Cards({results}) {

    let display;

    if(results)
    {
      display=results.map(x=>{
          let {id,name,image,location,gender,status,species}=x;
          return (

        //   <div className="col-4 position-relative mb-4" key={id}>
        // <div className={styles.cards}>
        // <img src={image} className={`${styles.img} img-fluid`} alt=""/>
        // <div className={styles.content}>
        // <div className="fs-4 fw-bold mb-4">{name}</div>
        //  <div className="fs-6">Location</div>
        // <div className="fs-5">{location.name}</div>
        // </div>
        // </div>
        // </div> 

          <div className={styles.flipcard}>
                <div className={styles.flipcardinner}>
                     <div class={styles.flipcardfront}>
                          <img src={image} className={`${styles.img} img-fluid`} alt=""/>
                          </div>
                          <div className={styles.flipcardback}>
                           <div className={styles.content}> 
                            <div className="fs-3 fw-bold mb-1">{name}</div>
                            <div className="fs-7">Location</div>
                            <div className="fs-5 fw-bold">{location.name}</div>
                            <div className="fs-6">Gender</div>
                            <div className="fs-5 fw-bold">{gender}</div>
                            <div className="fs-6">Species</div>
                            <div className="fs-5 fw-bold">{species}</div>
                            <div className="fs-6">Status</div>
                            <div className="fs-5 fw-bold">{status}</div> 

                           </div> 
                        </div>
                  </div>
          </div>


      )});
    }
    else{
        display="No Characters Found :/"
    }
    return (
            <>{display}</>
    )
}

export default Cards;
