import React,{useState,useEffect} from 'react'

const Favourites = () => {

    const [array,setArray]=useState([]);
    const [fetchedData,updateFetchedData]=useState([]);
    let newArray;
    let {results}=fetchedData;
    useEffect(()=>{

        if(localStorage.getItem('liked')!=null)
        {
           setArray(JSON.parse(localStorage.getItem('liked'))) ;    
        }
    },[])

    newArray=array.toString()

    let api=`https://rickandmortyapi.com/api/character/${newArray}`

    useEffect(()=>{

        (async function(){
          let data= await fetch(api).then(res=>res.json());
           updateFetchedData(data);
        })()
      },[api])

   console.log(fetchedData)

   let display;
   if(results)
    {
      display=results.map(x=>{
          let {id,name,image,location,gender,status,species}=x;



          return (

          <div className={styles.flipcard} key={id}>
                <div className={styles.flipcardinner} >
                     <div className={styles.flipcardfront}>
                          <img src={image} className={`${styles.img}`} alt=""/>
                          </div>
                          <div className={styles.flipcardback}>
                           <div className={styles.content}> 
                            <div className="fs-3 fw-bold mb-1" style={{color:"black"}}>{name}</div>
                            <div className="fs-6">Location</div>
                            <div className="fs-6 fw-bold">{location.name}</div>
                            <div className="fs-6">Gender</div>
                            <div className="fs-6 fw-bold">{gender}</div>
                            <div className="fs-6">Species</div>
                            <div className="fs-6 fw-bold">{species}</div>
                            <div className="fs-6">Status</div>
                            <div className="fs-6 fw-bold">{status}</div> 
                            <button type="submit" onClick={()=>handleClick(x)}>Like</button>
                           </div> 
                        </div>
                  </div>
          </div>


      )});
    }
    else{
        display="No Characters Found :/"
    }
    return (
            <>{display}</>
    )
    }

export default Favourites;
